import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import Chart from 'chart.js/auto'; // Import Chart.js
import API from "../services/api";
import "../css/Diary.css";

export default function Diary() {
  const [entries, setEntries] = useState([]);
  const [newEntry, setNewEntry] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showGraph, setShowGraph] = useState(false);
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  const navigate = useNavigate();

  const getMoodEmoji = (mood) => {
    switch (mood) {
      case 'positive':
        return 'ðŸ˜Š';
      case 'negative':
        return 'ðŸ˜”';
      case 'neutral':
        return 'ðŸ˜';
      default:
        return 'ðŸ“';
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  // Function to create or update the sentiment graph
  const renderGraph = (data) => {
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    const ctx = chartRef.current.getContext('2d');
    chartInstance.current = new Chart(ctx, {
      type: 'line',
      data: {
        labels: data.map(entry => new Date(entry.date).toLocaleDateString()),
        datasets: [{
          label: 'Mood Score',
          data: data.map(entry => entry.sentiment.score),
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1,
          fill: false,
        }]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
            min: -1,
            max: 1,
          }
        }
      }
    });
  };

  useEffect(() => {
    const fetchEntries = async () => {
      setLoading(true);
      try {
        const res = await API.get("/diary");
        const sorted = res.data.sort(
          (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
        );
        setEntries(sorted);
        // Render the graph with fetched data
        renderGraph(res.data);
      } catch (err) {
        setError("Error fetching diary entries.");
        console.error("Error fetching diary:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchEntries();
  }, []);

  const handleAddEntry = async (e) => {
    e.preventDefault();
    if (!newEntry.trim()) return;

    try {
      const res = await API.post("/diary", { text: newEntry });
      // The backend should return the full entry including sentiment
      setEntries([res.data.entry, ...entries]); // Access the 'entry' field
      setNewEntry("");
      // Re-render the graph with the new data
      renderGraph([...entries, res.data.entry]);
    } catch (err) {
      setError("Error adding diary entry.");
      console.error("Error adding diary entry:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/diary/${id}`);
      const updatedEntries = entries.filter((entry) => entry._id !== id);
      setEntries(updatedEntries);
      renderGraph(updatedEntries);
    } catch (err) {
      setError("Error deleting entry.");
      console.error("Error deleting entry:", err);
    }
  };

  const handleEdit = (id, text) => {
    setEditingId(id);
    setEditText(text);
  };

  const handleUpdate = async (id) => {
    try {
      // Backend should handle the update and return the new entry
      const res = await API.put(`/diary/${id}`, { text: editText });
      const updatedEntries = entries.map((entry) => (entry._id === id ? res.data.entry : entry));
      setEntries(updatedEntries);
      setEditingId(null);
      setEditText("");
      renderGraph(updatedEntries);
    } catch (err) {
      setError("Error updating entry.");
      console.error("Error updating entry:", err);
    }
  };

  return (
    <div className="diary-container">
      <div className="diary-header">
        <h2>My Diary</h2>
        <div className="header-actions">
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      <form onSubmit={handleAddEntry} className="new-entry-form">
        <textarea
          placeholder="Write your thoughts..."
          value={newEntry}
          onChange={(e) => setNewEntry(e.target.value)}
        />
        <button type="submit">Add Entry</button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      
      <div className="graph-section">
        <button onClick={() => setShowGraph(!showGraph)}>
          {showGraph ? "Hide Graph" : "Show Graph"}
        </button>
        {showGraph && <canvas ref={chartRef} className="sentiment-graph"></canvas>}
      </div>

      <ul className="entries-list">
        {entries.map((entry) => (
          <li key={entry._id} className="entry">
            {editingId === entry._id ? (
              <div className="edit-box">
                <textarea
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                />
                <button onClick={() => handleUpdate(entry._id)}>Save</button>
                <button onClick={() => setEditingId(null)}>Cancel</button>
              </div>
            ) : (
              <div className="entry-content">
                <p>{entry.text}</p>
                <div className="entry-meta">
                  <small>{new Date(entry.createdAt).toLocaleString()}</small>
                  {entry.sentiment && (
                    <span className="sentiment-emoji">
                      {getMoodEmoji(entry.sentiment.score > 0 ? 'positive' : entry.sentiment.score < 0 ? 'negative' : 'neutral')}
                    </span>
                  )}
                </div>
                <div className="entry-actions">
                  <button onClick={() => handleEdit(entry._id, entry.text)}>
                    Edit
                  </button>
                  <button onClick={() => handleDelete(entry._id)}>
                    Delete
                  </button>
                </div>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
